import styled, { css } from 'styled-components';
import { Photo } from '../../styles/Icons';

export const Container = styled.div`
  display: flex; 
  align-content: space-between;
  flex-direction: column;
  padding: 3px;
  border-bottom: 1px solid var(--outline);
  max-width: 100%;
`;

export const Body = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 3px;
  position: relative;
`;

export const Content = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  margin-top: 2px;
  padding-left: 59px;
`;

export const Texto = styled.div`
  > textarea {
    min-width: 475px;
    max-width: 475px;
    min-height: 115px;
  }

`;


export const Bottom = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-wrap: wrap;
  margin: 11px auto 0;
  width: 100%; /** Mobile */

  @media (min-width: 330px) {
    width: 63%;
  }

  > div {
    cursor: pointer;

    &:hover {
      opacity: 0.7;
    }
  }
`;

export const Icon = styled.div`
  display: flex;
  align-items: center;
  font-size: 14px;

  > svg {
    margin-right: 5px;
  }

  &:nth-child(1) {
    &,
    > svg path {
      color: var(--gray);
    }
  }

  &:nth-child(2) {
    color: var(--like);

    > svg {
      fill: var(--like);
    }
  }
`;



const iconCSS = css`
  width: 19px;
  height: 19px;
`;

export const PhotoAlbum = styled(Photo)`
  ${iconCSS}
`;

export const  Button = styled.div`

`;
