import React from 'react'
import {AddPhotoAlternate} from 'styled-icons/material-outlined';
import { useState } from 'react';
import {addDoc, collection, serverTimestamp} from 'firebase/firestore';
import {db} from "../../firebase";

import {
    Container,
    Body,
    Content,
    Texto,
    Bottom,
    ButtonPost,
    Icon,
  } from './styles';

const Input = ({open, onClose} : any) =>  {
    if(!open) return null;
    const [input, setInput] = useState("");
    const postar = async()=>{
        const docRef = await addDoc(collection(db, "posts"),{
            text: input,
            timestamp: serverTimestamp(),
        });

        setInput("");
    };

        if(!open) return null;




    return (  
        <Container >
            <Body>
                <Content >
                    <Texto>
                        <textarea rows={2} placeholder="Criar um evento" value={input} onChange={(e)=>setInput(e.target.value)}/>
                    </Texto>

                    <Bottom>
                        <Icon> 
                            <AddPhotoAlternate height={25} width={25} />
                        </Icon>
                        <ButtonPost onClick={postar} disabled={!input.trim()}>
                            <span>Postar</span>
                        </ButtonPost>
                    </Bottom>
                </Content>
            </Body>
        </Container>

    );
};

export default Input;
